<?php/*Template Name: Redirect to first subpage*/if ( have_posts() ) {	while ( have_posts() ) {		the_post();		$subpages_args = array(			'sort_column'  => 'menu_order, post_title',			'child_of' => $post->ID		);		$subpages = get_pages( $subpages_args );		if ( $subpages ) {			$first_subpage = $subpages[0];			wp_redirect( get_permalink( $first_subpage->ID ) );		}	}}// there is no subpages, so lets show the content of the current page?><?php get_header(); ?><?php get_template_part( 'template-part', 'wrap-before' ); ?><?php echo '<!-- there is no subpages, so lets show the content of the current page -->'; ?>	<?php while ( have_posts() ) : the_post(); // the loop ?>		<?php get_template_part( 'content', 'page' ); ?>		<?php comments_template( '', true ); ?>	<?php endwhile; // end of the loop ?><?php get_template_part( 'template-part', 'wrap-after' ); ?><?php get_sidebar(); ?><?php get_footer(); ?>